mapper.select:
select no,user_id,content
from trip_view_info
where trip_view_info.no = #{no}

mapper.update:
update trip_info
title = #{title}, content = #{content}, admission = #{admission}, opentime = #{opentime}, city = #{city}, address = #{address}, postcode = #{postcode}, photo = #{photo}, on_off = #{on_off}, p_link = #{p_link}, up_view = #{up_view}, views = views+1, likes = likes+1,
where no=#{no}
