mapper.update:
update gh_room_info r inner join gh_more_info m on r.no = m.no inner join gh_info g on m.no = g.no
title = #{title}, content = #{content}, city = #{city}, city = #{address}, postcode = #{postcode}, on_off = #{on_off}, p_link = #{p_link}, up_view = #{up_view}, views = views+1, likes = likes+1, room = #{room}, price = #{price}, photo = #{photo}, room_people = #{room_people}, party_menu = #{party_menu}, alcohol = #{alcohol}, party_time = #{party_time}, off_time = #{off_time}, join_man = #{join_man}, join_girl = #{join_girl}, party_day = #{party_day}, wifi = #{wifi}, parking = #{parking}, morning = #{morning}, pet = #{pet}, check_in = #{check_in}, check_out = #{check_out},
where g.no=#{no} and r.room_status = #{room_status}

update gh_room_info
room = #{room}, price = #{price}, photo = #{photo}, room_people = #{room_people},
where no=#{no}

update gh_more_info
party_menu = #{party_menu}, alcohol = #{alcohol}, party_time = #{party_time}, off_time = #{off_time}, join_man = #{join_man}, join_girl = #{join_girl}, party_day = #{party_day}, wifi = #{wifi}, parking = #{parking}, morning = #{morning}, pet = #{pet}, check_in = #{check_in}, check_out = #{check_out},
where no=#{no}

mapper.select:
select no, room, price, photo, room_people, gender
from gh_room_info
where no = #{no} and room = #{room}
